import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
  const [colaboradores, setColaboradores] = useState([]);
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    cargo: '',
    telefone: '',
  });

  const [editandoId, setEditandoId] = useState(null);

  useEffect(() => {
    loadColaboradores();
  }, []);

  const loadColaboradores = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/colaboradores');
      setColaboradores(res.data);
    } catch (err) {
      console.error('Erro ao carregar colaboradores:', err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editandoId) {
        await axios.put(`http://localhost:3001/api/colaboradores/${editandoId}`, formData);
        setEditandoId(null);
      } else {
        await axios.post('http://localhost:3001/api/colaboradores', formData);
      }
      setFormData({ nome: '', email: '', cargo: '', telefone: '' });
      loadColaboradores();
    } catch (err) {
      console.error('Erro ao salvar colaborador:', err);
    }
  };

  const handleEdit = (colaborador) => {
    setFormData({
      nome: colaborador.nome,
      email: colaborador.email,
      cargo: colaborador.cargo,
      telefone: colaborador.telefone,
    });
    setEditandoId(colaborador.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/colaboradores/${id}`);
      loadColaboradores();
    } catch (err) {
      console.error('Erro ao excluir colaborador:', err);
    }
  };

  return (
    <div className="container mt-5">
      <h2>{editandoId ? 'Editar' : 'Cadastrar'} Colaborador</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="nome" value={formData.nome} onChange={handleChange} placeholder="Nome" required />
        <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
        <input type="text" name="cargo" value={formData.cargo} onChange={handleChange} placeholder="Cargo" required />
        <input type="text" name="telefone" value={formData.telefone} onChange={handleChange} placeholder="Telefone" required />
        <button type="submit" className="btn btn-primary mt-2">Salvar</button>
      </form>

      <h3 className="mt-5">Lista de Colaboradores</h3>
      <ul className="list-group">
        {colaboradores.map(colab => (
          <li key={colab.id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <strong>{colab.nome}</strong> - {colab.email} - {colab.cargo} - {colab.telefone}
            </div>
            <div>
              <button onClick={() => handleEdit(colab)} className="btn btn-warning btn-sm me-2">Editar</button>
              <button onClick={() => handleDelete(colab.id)} className="btn btn-danger btn-sm">Excluir</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
