import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [colaboradores, setColaboradores] = useState([]);
  const [form, setForm] = useState({
    nome: '',
    email: '',
    cargo: '',
    telefone: '',
    salario: '',
    data_admissao: '',
  });
  const [editId, setEditId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadColabs();
  }, []);

  const loadColabs = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/colaboradores');
      setColaboradores(response.data);
    } catch (error) {
      console.error('Erro ao carregar colaboradores:', error);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:3001/api/colaboradores/${editId}`, form);
      } else {
        await axios.post('http://localhost:3001/api/colaboradores', form);
      }
      setForm({ nome: '', email: '', cargo: '', telefone: '', salario: '', data_admissao: '' });
      setEditId(null);
      loadColabs();
    } catch (error) {
      console.error('Erro ao salvar colaborador:', error);
    }
  };

  const handleEdit = (colab) => {
    setForm(colab);
    setEditId(colab.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este colaborador?')) {
      await axios.delete(`http://localhost:3001/api/colaboradores/${id}`);
      loadColabs();
    }
  };

  return (
    <div className="container">
      <h2>Cadastro de Colaboradores</h2>

      {/* Barra de Pesquisa */}
      <input
        type="text"
        placeholder="Pesquisar por nome, email ou cargo..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="form-control mb-3"
      />

      {/* Formulário */}
      <form onSubmit={handleSubmit}>
        <input name="nome" placeholder="Nome" value={form.nome} onChange={handleChange} className="form-control mb-2" />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} className="form-control mb-2" />
        <input name="cargo" placeholder="Cargo" value={form.cargo} onChange={handleChange} className="form-control mb-2" />
        <input name="telefone" placeholder="Telefone" value={form.telefone} onChange={handleChange} className="form-control mb-2" />
        <input name="salario" placeholder="Salário" value={form.salario} onChange={handleChange} className="form-control mb-2" />
        <input name="data_admissao" type="date" value={form.data_admissao} onChange={handleChange} className="form-control mb-2" />

        <button type="submit" className="btn btn-primary">
          {editId ? 'Atualizar' : 'Salvar'}
        </button>
      </form>

      {/* Lista de Colaboradores */}
      <table className="table table-striped mt-4">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Cargo</th>
            <th>Telefone</th>
            <th>Salário</th>
            <th>Data de Admissão</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {colaboradores
            .filter(colab =>
              colab.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
              colab.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
              colab.cargo.toLowerCase().includes(searchTerm.toLowerCase())
            )
            .map((colab) => (
              <tr key={colab.id}>
                <td>{colab.nome}</td>
                <td>{colab.email}</td>
                <td>{colab.cargo}</td>
                <td>{colab.telefone}</td>
                <td>{colab.salario}</td>
                <td>{colab.data_admissao}</td>
                <td>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(colab)}>Editar</button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDelete(colab.id)}>Excluir</button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
