// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import ColaboradorForm from './ColaboradorForm';

const Dashboard = () => {
  const [colabs, setColabs] = useState([]);
  const [formData, setFormData] = useState({
    nome: '',
    cpf: '',
    email: '',
    telefone: '',
    cargo: ''
  });
  const [editId, setEditId] = useState(null);

  // 1️⃣ Carrega a lista assim que o componente monta
  useEffect(() => {
    loadColabs();
  }, []);

  // 2️⃣ Função que busca todos os colaboradores
  const loadColabs = async () => {
    try {
      const res = await api.get('/colaboradores');
      setColabs(res.data);
    } catch (err) {
      console.error('Erro ao carregar colaboradores:', err);
      setColabs([]);
    }
  };

  // 3️⃣ Atualiza o formData conforme o usuário digita
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // 4️⃣ Quando clica em “Salvar” (inclusão ou edição)
  const handleSubmit = async (e) => {
    e.preventDefault(); // evita reload da página
    try {
      if (editId) {
        // se editId != null, faz PUT /colaboradores/:id
        await api.put(`/colaboradores/${editId}`, formData);
      } else {
        // se editId == null, faz POST /colaboradores
        await api.post('/colaboradores', formData);
      }
      // após salvar, limpa o formulário e reseta editId
      setFormData({ nome: '', cpf: '', email: '', telefone: '', cargo: '' });
      setEditId(null);
      // e carrega a lista atualizada
      await loadColabs();
    } catch (err) {
      console.error('Erro ao salvar colaborador:', err);
      alert('Não foi possível salvar o colaborador.');
    }
  };

  // 5️⃣ Preenche o formulário para edição
  const handleEdit = (colab) => {
    setFormData({
      nome: colab.nome,
      cpf: colab.cpf,
      email: colab.email,
      telefone: colab.telefone,
      cargo: colab.cargo
    });
    setEditId(colab.id);
  };

  // 6️⃣ Exclui o colaborador e recarrega a lista
  const handleDelete = async (id) => {
    if (window.confirm('Deseja excluir este colaborador?')) {
      try {
        await api.delete(`/colaboradores/${id}`);
        await loadColabs();
      } catch (err) {
        console.error('Erro ao excluir colaborador:', err);
        alert('Não foi possível excluir.');
      }
    }
  };

  return (
    <div className="container mt-4">
      <h2>Gerenciamento de Colaboradores</h2>

      {/* Formulário de inclusão/edição */}
      <ColaboradorForm
        formData={formData}
        onChange={handleChange}
        onSubmit={handleSubmit}
      />

      {/* Lista de colaboradores */}
      {colabs.length === 0 ? (
        <p className="text-muted">Nenhum colaborador encontrado.</p>
      ) : (
        colabs.map((c) => (
          <div key={c.id} className="mb-2 border p-2 rounded">
            <strong>{c.nome.toUpperCase()}</strong> — {c.cargo} — {c.email}
            <br />
            <button
              className="btn btn-primary btn-sm me-2 mt-1"
              onClick={() => handleEdit(c)}
            >
              Editar
            </button>
            <button
              className="btn btn-danger btn-sm mt-1"
              onClick={() => handleDelete(c.id)}
            >
              Excluir
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default Dashboard;
