// pastafrontend/src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import ColaboradorForm from './ColaboradorForm';

const Dashboard = () => {
  const [colabs, setColabs] = useState([]);
  const [formData, setFormData] = useState({
    nome: '',
    cpf: '',
    email: '',
    telefone: '',
    cargo: ''
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    load();
  }, []);

  const load = async () => {
    try {
      const res = await api.get('/colaboradores');
      setColabs(res.data);
    } catch (err) {
      console.error('Erro ao carregar colaboradores:', err);
      alert('Não foi possível carregar os colaboradores.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/colaboradores/${editId}`, formData);
      } else {
        await api.post('/colaboradores', formData);
      }
      setFormData({ nome: '', cpf: '', email: '', telefone: '', cargo: '' });
      setEditId(null);
      load();
    } catch (err) {
      console.error('Erro ao salvar colaborador:', err);
      alert('Não foi possível salvar o colaborador.');
    }
  };

  const handleEdit = (colab) => {
    setFormData(colab);
    setEditId(colab.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Deseja excluir este colaborador?')) {
      try {
        await api.delete(`/colaboradores/${id}`);
        load();
      } catch (err) {
        console.error('Erro ao excluir colaborador:', err);
        alert('Não foi possível excluir.');
      }
    }
  };

  return (
    <div className="container mt-4">
      <h2>Gerenciamento de Colaboradores</h2>
      <ColaboradorForm
        formData={formData}
        onChange={handleChange}
        onSubmit={handleSubmit}
      />
      {colabs.map((c) => (
        <div key={c.id} className="mb-2 border p-2 rounded">
          <strong>{c.nome.toUpperCase()}</strong> — {c.cargo} — {c.email}
          <br />
          <button
            className="btn btn-primary btn-sm me-2 mt-1"
            onClick={() => handleEdit(c)}
          >
            Editar
          </button>
          <button
            className="btn btn-danger btn-sm mt-1"
            onClick={() => handleDelete(c.id)}
          >
            Excluir
          </button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
