import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import ColaboradorForm from './ColaboradorForm';

const Dashboard = () => {
  const navigate = useNavigate();
  const [colabs, setColabs] = useState([]);
  const [formData, setFormData] = useState({ nome: '', cpf: '', email: '', telefone: '', cargo: '' });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    load();
  }, []);

  const load = async () => {
    try {
      const res = await api.get('/colaboradores');
      setColabs(res.data);
    } catch {
      alert('Erro ao carregar dados. Faça login.');
      navigate('/');
    }
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/colaboradores/${editId}`, formData);
      } else {
        await api.post('/colaboradores', formData);
      }
      setFormData({ nome: '', cpf: '', email: '', telefone: '', cargo: '' });
      setEditId(null);
      load();
    } catch {
      alert('Erro ao salvar');
    }
  };

  const handleEdit = (colab) => {
    setFormData(colab);
    setEditId(colab.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Deseja excluir?')) {
      await api.delete(`/colaboradores/${id}`);
      load();
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="container mt-4">
      <h2>Gerenciamento de Colaboradores</h2>
      <button className="btn btn-secondary mb-3" onClick={logout}>Sair</button>
      <ColaboradorForm formData={formData} onChange={handleChange} onSubmit={handleSubmit} />
      {colabs.map(c => (
        <div key={c.id} className="mb-2 border p-2 rounded">
          <strong>{c.nome.toUpperCase()}</strong> — {c.cargo} — {c.email}
          <br />
          <button className="btn btn-primary btn-sm me-2 mt-1" onClick={() => handleEdit(c)}>Editar</button>
          <button className="btn btn-danger btn-sm mt-1" onClick={() => handleDelete(c.id)}>Excluir</button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
